import {Form,Container,Button} from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router";
import { userActions } from '../action/userAction';
import { readingActions } from '../action/ReadingAction';
import { qtActions } from '../action/QtAction';
import AdminCard from '../component/AdminCard';

const Admin = () => {
  const dispatch=useDispatch()
  const navigate=useNavigate()
const [openR,setOpenR]=useState(true);
const [saveItems,setSaveItems]=useState(null)
const {adminAll}= useSelector(state=>state.reading)

// 이름을 admin 에서 찾는다
// 이름을 클릭하면 숙제가 나오게 한다.
// -event function 만들기 -

function findByName(name){
  let result =adminAll && adminAll?.filter((item)=>item.userId.name === name )
  console.log(result,'result!!!!!!!!!')
  return setSaveItems(result)

}

useEffect(()=>{
  dispatch(readingActions.adminShowAll())

},[dispatch])

console.log(saveItems,'saveitems')
  return (
    <div>
      <h3>제자도 읽기</h3>
      <div>
        {
        adminAll && adminAll?.map(item=>(
        <h3 key={item._id} onClick={()=>findByName(item.userId.name)}>{item.userId.name}</h3>


        ))
        }
    <div>{saveItems && saveItems?.map(item=>(<AdminCard/>))}</div>
      </div>
    </div>
  )
}

export default Admin
